from itertools import count
import anywidget, time
from IPython.display import DisplayHandle
from bridget.helpers import displaydh
# from bridget.helpers import skip
import ipywidgets as W

counter = count()

class TestWidget(anywidget.AnyWidget):
    _esm = '''debugger;
export default { 
    async initialize({ model }) {
        function on_msg(msg) {
            console.log(`++++ Received message:`, msg);
            if (window.$Nb) {
                msg.origin = document.location.href;
                window.$Nb.update(msg);
                return;
            }
            return model.send({ id: msg.id, response: 'no response' });
        }
        model.on("msg:custom", on_msg);
        function on_stateChange(chg) {
            console.log('++++ on_state', chg.reqId, chg);
            model.send({ changes: chg });
        }
        let cleanup;
        if (window.$Nb) {
            cleanup = window.$Nb.addStateObserver(on_stateChange);
        }
        if (cleanup) return cleanup;
    }
};
    '''
    dh: DisplayHandle
    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)
        self.on_msg(self._handle_message)
        self.out = W.Output()
        display(self.out)
        with self.out:
            print('Test widget, cell 35')

    def _handle_message(self, w, msg, buffers):
        # self.dh.update(f'Received {msg=}, {buffers=}', metadata=skip(cell=35))
        self.msg = msg
        with self.out:
            print(f'Received {msg=}, {buffers=}')

w = TestWidget()
